package com.syntax.class09;

public class Patterns {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		// how many rows I have: 5 how many rows I have:5
		for(int i = 5; i>=1; i++) {
			for(int j=i; j>=1; j++) {
				System.out.print("* ");
			}
			System.out.println();
		}

	}

}
